import { createFeatureSelector, createSelector } from '@ngrx/store';
const selectDepartmentsState = createFeatureSelector('departments');
export const selectAllDepartments = createSelector(selectDepartmentsState, state => state.departments);
export const selectSelectedDepartment = createSelector(selectDepartmentsState, state => state.selectedDepartment);
export const selectDepartmentsLoading = createSelector(selectDepartmentsState, state => state.loading);
export const selectDepartmentsError = createSelector(selectDepartmentsState, state => state.error);
export const selectDepartmentById = id => createSelector(selectAllDepartments, departments => departments.find(d => d.id === id) || null);
export const selectDepartmentCount = createSelector(selectAllDepartments, departments => departments.length);
