import { createFeatureSelector, createSelector } from '@ngrx/store';
const selectRolesState = createFeatureSelector('roles');
export const selectAllRoles = createSelector(selectRolesState, state => state.roles);
export const selectSelectedRole = createSelector(selectRolesState, state => state.selectedRole);
export const selectRolesLoading = createSelector(selectRolesState, state => state.loading);
export const selectRolesError = createSelector(selectRolesState, state => state.error);
export const selectRoleById = id => createSelector(selectAllRoles, roles => roles.find(r => r.id === id) || null);
export const selectRoleCount = createSelector(selectAllRoles, roles => roles.length);
