import { createSelector } from '@ngrx/store';
export const selectUnitsState = state => state.units;
export const selectAllUnits = createSelector(selectUnitsState, state => state.units);
export const selectSelectedUnit = createSelector(selectUnitsState, state => state.selectedUnit);
export const selectUnitsLoading = createSelector(selectUnitsState, state => state.loading);
export const selectUnitsError = createSelector(selectUnitsState, state => state.error);
export const selectUnitById = id => createSelector(selectAllUnits, units => units.find(unit => unit.id === id));
export const selectUnitCount = createSelector(selectAllUnits, units => units.length);
export const selectUnitsByCity = city => createSelector(selectAllUnits, units => units.filter(unit => unit.city === city));
export const selectUnitsByState = state => createSelector(selectAllUnits, units => units.filter(unit => unit.state === state));
