import { createFeatureSelector, createSelector } from '@ngrx/store';
const selectEmployeesState = createFeatureSelector('employees');
export const selectAllEmployees = createSelector(selectEmployeesState, state => state.employees);
export const selectSelectedEmployee = createSelector(selectEmployeesState, state => state.selectedEmployee);
export const selectEmployeesLoading = createSelector(selectEmployeesState, state => state.loading);
export const selectEmployeesError = createSelector(selectEmployeesState, state => state.error);
export const selectEmployeeById = id => createSelector(selectAllEmployees, employees => employees.find(e => e.id === id) || null);
export const selectEmployeeCount = createSelector(selectAllEmployees, employees => employees.length);
